mapper.update:
UPDATE tb_subject
SET status = 1
WHERE subid=#{subid}

UPDATE tb_subject
subname = #{subject.subname}, usedyear = #{subject.usedyear}, subkind = #{subject.subkind}, subsource = #{subject.subsource}, subtype = #{subject.subtype}, isoutschool = #{subject.isoutschool}, tid = #{subject.tid}, asstid = #{subject.asstid}, status = #{subject.status}, subdirection = #{subject.subdirection}, subsort = #{subject.subsort}, summary = #{subject.summary}, content = #{subject.content}, requirement = #{subject.requirement}, refpapers = #{subject.refpapers}, subprog = #{subject.subprog}
WHERE subid=#{subject.subid}

mapper.select:
select count(subid)
from tb_subject
where tid = #{tid}

select count(tid)
from tb_reviewsubject
where tid = #{tid}

select count(tid)
from tb_reviewsubject
where tid = #{tid} and reviewopinion is null or reviewopinion='';

SELECT *
FROM tb_subject
WHERE   subname=#{subname} AND tid=#{tid}

SELECT count(*)
FROM tb_subject
WHERE tid=#{tid}

SELECT NULLIF(reviewopinion,'')
FROM tb_reviewsubject
WHERE subid=#{subid}

SELECT usedyear,subname
FROM tb_subhistory
WHERE tid=#{tid} AND usedyear > #{longyear}
